  , fs = require('fs')

function handleStatic(opts, io, nextHandler) {
  var cwd = opts.cwd || process.cwd()

  return handle

  function handle(server, req, resp, parsed) {
    var filepath = path.join.apply(
        path
      , [cwd].concat(parsed.pathname.split('/'))
    )

