    if(/html/.test(req.headers.accept || '')) {
      check.push(path.join(filepath, 'index.html'))
      check.push(filepath + '.html')
    }

    return iter()

    function iter() {
      if(!check.length) {
        return nextHandler(server, req, resp, parsed)
